package com.syntax.class01;

public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//storing and representing numeric values;
		byte box1 =127;  // represents any number from -128 to 127 -> small
		
		short box2 =32767;  // represents any number from -32768 to 32767 -> medium
		
		 int box3 =10000; // represents any number from -214748648 to 2144748647 -> big (popular)
		 
		 long box4 =1000000L; // represents any number from - 9,223,372,036,854,775,808 to 9223 etc -> bigger
		 
		 //storing and representing decimal values;
		 float variable1=1.99F;
		 
		 double variable2=99.99;  // popular
		 
		 //storing single characters
		 char container='A';
		 char dollar='$';
		 
		 //sorting yes or no values -> True or False
		 boolean hot=true;
		 boolean tired=false;
		 
		 System.out.println (variable2); //printing value of the variable
		 System.out.println ("variable2"); //printing just the text variable2
		 
		 System.out.println(tired);
		 
		 
		 
		 
		 
		 
	}

}
