package com.syntax.class02;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// +, -, *, /, and % --> these are Operators in Java aka Arithmetic Operators
		//+ for addition, - for subtraction, * for multiplication, / for division, % for MOD -For Remainder
		
		int a=900;
		int b=100;
		
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		
		int c=10;
		int d=3;
		
		int sum=c+d;
		int sub=c-d;
		int remainder =c%d; //out put will be 1
		int div =c/d; //out put will be 3
		System.out.println("sum is = "+sum);
		System.out.println("sub is = "+sub);
		System.out.println("Remainder is "+remainder);
		System.out.println("div is = "+div); // out put will be 3
		
		double num1=10.0;
		double num2=3.0;
		
		System.out.println(num1/num2); //out put will be 3.3333333333333335
		
		
		
		int e=10;
		int f=5;
		
		System.out.println("division = "+e/f); //out put will be 2
		System.out.println("remainder = "+e%f); //out put will be 0
		
		System.out.println(10+10/10); //out put will be 11
		System.out.println((10+10)/10); //out put will be 2
		
		
		
		
		
		
		
		
		

	}

}
