package com.syntax.class02;

public class VariableCreation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//First  way to create a variable
		byte a=10; //created a variable and assigned value to it (10)
		
		//2nd way to create a variable
		int i; //create a variable
		i=100; //assign value
		
		//variable creation only happens once
		//then we can use as many times we want/need during the program
		
		//3rd way to create a variable
		//create a variable is the same as saying = declare a variable
		int num1, num2, num3;
		
		num1=101; //assign  value or initialize  variable -> means the same thing
		num2=202;
		num3=303;
		
		
		//int num1=101; this is wrong/error cuz duplicate variable
		
		num2=2000; //you can change the value of a variable as long as its the same data type
		// its called reassign the value

		
		System.out.println (a);
		System.out.println(i);
		System.out.println (num1);
		System.out.println (num2);
		System.out.println (num3);
		
		
		//Non primitive data type
		
		//String is one of the non primitive data type -> a value that represented in ""
		
		
		
		
		
		
		
		
	}

}
