package com.syntax.class02;

public class Variables2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	String name = "Fraidoon";
	String lastname = "Hussain";
	int grade = 12;
	String city = "Reston";
	String state = "VA";
	String phonenumber = "571-123-4567";
	
	System.out.println("My name is "+name + " and my last name is " +lastname);
	System.out.println("I am A student");
	System.out.println("I live in the city "+city + " and state " +state);
	System.out.println("And my phone number is "+ phonenumber);
	
	
	
	city = "Herndon"; //reassigning
	state = "Maryland";
	phonenumber = "123 456 6780";
	grade = 11;
	
	System.out.println("I moved to the new the city "+city + " and new state " +state);
	System.out.println("And my new phone number is "+ phonenumber);
	
	
	
			
		
	}

}
